import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  BookmarkIcon,
  FaceSmileIcon,
  HandRaisedIcon,
  XMarkIcon,
} from '@heroicons/react/24/solid';
import { useFavorites } from '../components/FavoritesContext';
import NavBar from '../components/NavBar';
import SaveButton from '../components/SaveButton';

const categoryMeta = {
  newsletter: {
    label: 'Newsletter',
    color: 'bg-brand-red',
    icon: BookmarkIcon,
  },
  feed: {
    label: 'Feed',
    color: 'bg-brand-orange',
    icon: FaceSmileIcon,
  },
  desafios: {
    label: 'Desafios',
    color: 'bg-brand-yellow',
    icon: HandRaisedIcon,
  },
};

const Favorites = () => {
  const navigate = useNavigate();
  const { favorites, toggleFavorite, isFavorited } = useFavorites();
  const [tab, setTab] = useState('newsletter');

  const meta = categoryMeta[tab];
  const TabIcon = meta.icon;

  return (
    <div className="min-h-screen bg-white pb-32">
      <header className={`px-6 pt-6 pb-4 rounded-b-3xl ${meta.color} text-white relative`}>
        <button
          onClick={() => navigate(-1)}
          className="h-12 w-12 rounded-full bg-white text-gray-700 flex items-center justify-center shadow"
          aria-label="Fechar"
        >
          <XMarkIcon className="h-7 w-7" />
        </button>
        <h1 className="mt-4 text-2xl font-bold flex items-center gap-2">
          Meus Favoritos <TabIcon className="h-6 w-6" />
        </h1>

        {/* Mini navbar canto superior direito */}
        <div className="absolute top-6 right-6 flex items-center gap-2">
          {Object.keys(categoryMeta).map(key => {
            const Icon = categoryMeta[key].icon;
            const active = tab === key;
            return (
              <button
                key={key}
                onClick={() => setTab(key)}
                className={`h-10 w-10 rounded-full flex items-center justify-center transition ${
                  active ? 'bg-white text-gray-800 shadow-md scale-105' : 'bg-white/25 text-white'
                }`}
                aria-label={categoryMeta[key].label}
              >
                <Icon className="h-6 w-6" />
              </button>
            );
          })}
        </div>
      </header>

      <main className="px-6 mt-6">
        <div className="bg-gray-50 rounded-3xl p-5">
          <h2 className="text-lg font-semibold text-gray-700 mb-4">
            {categoryMeta[tab].label}
          </h2>

          {/* Lista de conteÃºdo favoritado por aba */}
          {tab === 'newsletter' && (
            <div className="space-y-5">
              {favorites.newsletter.length === 0 && (
                <p className="text-sm text-gray-500">Nenhuma newsletter favoritada ainda.</p>
              )}
              {favorites.newsletter.map(item => (
                <div
                  key={item.id}
                  className="bg-white rounded-xl shadow flex flex-col overflow-hidden"
                >
                  <div className="relative">
                    <img
                      src={item.image}
                      alt={item.title}
                      className="w-full h-40 object-cover"
                    />
                    <div className="absolute top-2 right-2 bg-white/90 text-gray-800 text-xs font-semibold px-2 py-1 rounded-full">
                      {item.date}
                    </div>
                    <SaveButton
                      size={22}
                      saved={isFavorited('newsletter', item.id)}
                      onChange={() => toggleFavorite('newsletter', item)}
                      className="absolute bottom-2 right-2 bg-white"
                    />
                  </div>
                  <div className="p-4">
                    <h3 className="text-sm font-bold text-gray-800">{item.title}</h3>
                    <p className="text-xs text-gray-500 mt-1">{item.category}</p>
                  </div>
                </div>
              ))}
            </div>
          )}

          {tab === 'feed' && (
            <div className="space-y-5">
              {favorites.feed.length === 0 && (
                <p className="text-sm text-gray-500">Nenhum post do feed favoritado ainda.</p>
              )}
              {favorites.feed.map(post => (
                <div key={post.id} className="bg-gray-200 rounded-xl p-4">
                  <div className="flex items-start gap-3">
                    <div className="h-10 w-10 rounded-full bg-gray-900 text-white flex items-center justify-center text-sm">
                      ðŸ‘¤
                    </div>
                    <div className="flex-1">
                      <p className="text-sm font-semibold text-gray-800 leading-tight">
                        {post.name} <span className="text-amber-400">â˜…</span>
                      </p>
                      <p className="text-[11px] text-gray-500 -mt-0.5">{post.role}</p>
                      <p className="text-xs text-gray-700 mt-2 leading-snug">{post.text}</p>
                    </div>
                    <SaveButton
                      size={20}
                      saved={isFavorited('feed', post.id)}
                      onChange={() => toggleFavorite('feed', post)}
                    />
                  </div>
                </div>
              ))}
            </div>
          )}

          {tab === 'desafios' && (
            <div className="space-y-5">
              {favorites.desafios.length === 0 && (
                <p className="text-sm text-gray-500">Nenhum desafio favoritado ainda.</p>
              )}
              {favorites.desafios.map(ch => (
                <div key={ch.id} className="bg-gray-200 rounded-xl p-4">
                  <p className="text-sm font-semibold text-gray-800">{ch.title}</p>
                  <p className="text-xs text-gray-600 mt-1">{ch.snippet}</p>
                  <div className="mt-3 flex justify-end">
                    <SaveButton
                      size={20}
                      saved={isFavorited('desafios', ch.id)}
                      onChange={() => toggleFavorite('desafios', ch)}
                    />
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>

      <NavBar />
    </div>
  );
};

export default Favorites;
