import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { CubeTransparentIcon, CalendarDaysIcon, HandRaisedIcon, UsersIcon } from '@heroicons/react/24/solid';

const slides = [
  {
    id: 0,
    title: ['Bem vindo(a)', 'ao Fractal!'],
    icon: CubeTransparentIcon,
    text: 'Chega de fórmulas prontas. Nosso app rompe o ensino linear.'
  },
  {
    id: 1,
    title: ['Alimentação diária', 'de repertório:'],
    icon: CalendarDaysIcon,
    text: 'Receba doses diárias de conhecimento.'
  },
  {
    id: 2,
    title: ['Dinâmicas e', 'Desafios (IA)'],
    icon: HandRaisedIcon,
    text: 'Problemas não-lineares para justificar escolhas.'
  },
  {
    id: 3,
    title: ['Comunidade', 'e Portfólio'],
    icon: UsersIcon,
    text: 'Publique, receba feedback e evolua.',
    final: true
  }
];

const Welcome = () => {
  const navigate = useNavigate();
  const [active, setActive] = useState(0);
  const ref = useRef(null);
  const onScroll = e => {
    const w = e.target.clientWidth;
    setActive(Math.round(e.target.scrollLeft / w));
  };

  return (
    <div className="bg-white min-h-screen flex flex-col">
      <div
        ref={ref}
        onScroll={onScroll}
        className="flex flex-1 overflow-x-auto snap-x snap-mandatory scroll-smooth no-scrollbar"
      >
        {slides.map(s => {
          const Icon = s.icon;
            return (
            <section key={s.id} className="snap-center w-full flex-shrink-0 flex flex-col items-center justify-center px-10">
              <div className="max-w-sm text-center">
                <div className="mx-auto mb-8 rounded-xl flex items-center justify-center h-40 w-40 bg-brand-red/90">
                  <Icon className="h-24 w-24 text-white" />
                </div>
                <h1 className="text-3xl font-semibold mb-6">
                  {s.title.map((line, i) => <span key={i} className="block">{line}</span>)}
                </h1>
                <p className="text-sm text-gray-700">{s.text}</p>
                {s.final && (
                  <button
                    onClick={() => navigate('/newsletter')}
                    className="mt-10 w-full bg-brand-red text-white font-semibold py-3 rounded-full shadow"
                  >
                    Entrar
                  </button>
                )}
              </div>
            </section>
          );
        })}
      </div>
      <div className="flex items-center justify-center gap-3 py-6">
        {slides.map(s => (
          <span
            key={s.id}
            className={`h-2 w-2 rounded-full ${active === s.id ? 'bg-brand-red scale-125' : 'bg-gray-300'} transition`}
          />
        ))}
      </div>
    </div>
  );
};

export default Welcome;
