import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import {
  Cog6ToothIcon,
  BellIcon,
  ChatBubbleLeftRightIcon,
  HeartIcon,
  HandRaisedIcon,
  BookmarkIcon,
} from '@heroicons/react/24/solid';
import { Squares2X2Icon, UserCircleIcon } from '@heroicons/react/24/outline';

const Card = ({ to, bgColor, title, subtitle }) => (
  <Link to={to} className={`block p-6 rounded-3xl text-white ${bgColor} shadow-lg`}>
    <h3 className="text-2xl font-bold">{title}</h3>
    <p className="opacity-90">{subtitle}</p>
  </Link>
);

const msgs = [
  { id: 1, name: 'Marisa', text: 'Amei o resultado do seu desafio! ...', avatar: '/imgs/av1.png' },
  { id: 2, name: 'Julio', text: 'Oi cara! Consegue me ajudar a res...', avatar: '/imgs/av2.png' },
  { id: 3, name: 'Charles', text: 'vdd, consegui usar o newsletter de...', avatar: '/imgs/av3.png' },
  { id: 4, name: 'Flávio', text: 'Quanto tempo vc demorou pra term...', avatar: '/imgs/av4.png' },
  { id: 5, name: 'Leila', text: 'Oie, vi seu feedback no meu desafí...', avatar: '/imgs/av5.png' },
];

const notifs = [
  { id: 1, icon: HeartIcon, color: 'text-rose-500', title: 'Marisa', text: 'Curtiu seu Insight.' },
  { id: 2, icon: HandRaisedIcon, color: 'text-amber-500', title: 'Desafio de hoje liberado!', text: '' },
  { id: 3, icon: BookmarkIcon, color: 'text-brand-red', title: 'Acesse a Newsletter de hoje!', text: '' },
  { id: 4, icon: ChatBubbleLeftRightIcon, color: 'text-gray-700', title: 'Julio', text: 'Comentou no seu desafio.' },
];

// NOVO: itens da gaveta
const drawerItems = [
  { id: 'newsletter', title: 'Newsletter', subtitle: 'Aumente seu repertório!', to: '/newsletter', color: 'bg-brand-red' },
  { id: 'feed',       title: 'Feed',       subtitle: 'Acesse insights!',       to: '/feed',       color: 'bg-brand-teal' },
  { id: 'desafios',   title: 'Desafios',   subtitle: 'Aprimore seus processos!',to: '/challenges', color: 'bg-brand-orange' },
  { id: 'comunidade', title: 'Comunidade', subtitle: 'Dê e receba feedbacks!',  to: '/community',  color: 'bg-brand-yellow' },
];

// Cartão com animação de expansão para a “gaveta”
const DrawerCard = ({ item, index, expanded, onClick }) => {
  // sobreposição (gaveta): cada item sobe um pouco sobre o de baixo
  const stackOffset = index === 0 ? 0 : -28; // px
  // z-index para manter a ordem visual (o expandido fica no topo)
  const baseZ = 10 - index;
  const zTop = 50;

  return (
    <div
      onClick={onClick}
      style={{ marginTop: stackOffset, zIndex: expanded ? zTop : baseZ }}
      className={[
        'relative select-none cursor-pointer rounded-3xl shadow-lg text-white',
        item.color,
        // tamanhos e animação
        'transition-all duration-300 ease-out',
        expanded ? 'h-44 translate-y-[-8px] ring-2 ring-white/70' : 'h-24 translate-y-0',
        'px-6 flex items-center',
      ].join(' ')}
    >
      <div className="w-full">
        <h3 className={`font-bold ${expanded ? 'text-3xl' : 'text-2xl'}`}>{item.title}</h3>
        <p className={`opacity-90 ${expanded ? 'mt-1 block' : 'hidden'}`}>{item.subtitle}</p>

        {expanded && (
          <div className="absolute right-4 bottom-4">
            <Link
              to={item.to}
              onClick={(e) => e.stopPropagation()}
              className="bg-white/90 text-gray-800 text-sm font-semibold px-4 py-2 rounded-full shadow"
            >
              Abrir
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

const Dashboard = () => {
  const [view, setView] = useState('home'); // 'home' | 'messages' | 'notifications'
  const [expandedId, setExpandedId] = useState('newsletter');
  const navigate = useNavigate();

  return (
    <div className="bg-gray-100 min-h-screen flex flex-col">
      {/* Header */}
      <header className="p-8 flex justify-between items-center">
        <h2 className="text-3xl font-bold text-gray-800">Olá, Carlos!</h2>
        <Link to="/profile">
          <Cog6ToothIcon className="h-8 w-8 text-gray-700" />
        </Link>
      </header>

      {/* Main */}
      <main className="px-6 pb-24 flex-grow">
        {view === 'home' && (
          <section aria-label="gaveta de atalhos" className="relative">
            {drawerItems.map((item, idx) => (
              <DrawerCard
                key={item.id}
                item={item}
                index={idx}
                expanded={expandedId === item.id}
                onClick={() => setExpandedId(item.id)}
              />
            ))}
          </section>
        )}

        {view === 'messages' && (
          <section className="space-y-4">
            <div className="bg-white rounded-3xl p-5 shadow">
              <div className="flex flex-col items-center mb-4">
                <ChatBubbleLeftRightIcon className="h-10 w-10 text-brand-red mb-2" />
                <h3 className="text-2xl font-semibold text-gray-800">Suas mensagens</h3>
              </div>

              <ul className="space-y-3">
                {msgs.map((m) => (
                  <li key={m.id} className="flex items-center gap-3 bg-gray-100 rounded-2xl px-3 py-2">
                    <img
                      src={m.avatar}
                      alt={m.name}
                      onError={(e) => { e.currentTarget.src = 'https://via.placeholder.com/40'; }}
                      className="h-10 w-10 rounded-full object-cover"
                    />
                    <div className="flex-1">
                      <p className="text-sm font-semibold text-gray-800 leading-tight">{m.name}</p>
                      <p className="text-sm text-gray-600 truncate">{m.text}</p>
                    </div>
                    <span className="text-gray-400 text-lg">…</span>
                  </li>
                ))}
              </ul>
            </div>
          </section>
        )}

        {view === 'notifications' && (
          <section className="space-y-4">
            <div className="bg-white rounded-3xl p-5 shadow">
              <div className="flex flex-col items-center mb-4">
                <BellIcon className="h-10 w-10 text-brand-red mb-2" />
                <h3 className="text-2xl font-semibold text-gray-800">Suas notificações</h3>
              </div>

              <ul className="space-y-3">
                {notifs.map((n) => {
                  const Ico = n.icon;
                  return (
                    <li key={n.id} className="flex items-center gap-3 bg-gray-100 rounded-2xl px-3 py-3">
                      <Ico className={`h-6 w-6 ${n.color}`} />
                      <div className="flex-1">
                        <p className="text-sm font-semibold text-gray-800 leading-tight">
                          {n.title}
                        </p>
                        {n.text && <p className="text-sm text-gray-600">{n.text}</p>}
                      </div>
                    </li>
                  );
                })}
              </ul>
            </div>
          </section>
        )}
      </main>

      {/* Bottom nav */}
      <footer className="fixed left-0 right-0 bottom-4 mx-auto max-w-md m-0 p-4 bg-white rounded-full shadow-lg flex justify-around items-center">
        <button
          aria-label="Mensagens"
          onClick={() => setView('messages')}
          className={`h-7 w-7 ${view === 'messages' ? 'text-brand-red' : 'text-gray-500'}`}
        >
          <ChatBubbleLeftRightIcon />
        </button>

        <button
          aria-label="Notificações"
          onClick={() => setView('notifications')}
          className={`h-7 w-7 ${view === 'notifications' ? 'text-brand-red' : 'text-gray-500'}`}
        >
          <BellIcon />
        </button>

        <button
          aria-label="Início"
          onClick={() => setView('home')}
          className="h-10 w-10 bg-brand-red rounded-lg flex items-center justify-center"
        >
          <Squares2X2Icon className="h-6 w-6 text-white" />
        </button>

        <Link to="/profile" aria-label="Perfil">
          <UserCircleIcon className="h-7 w-7 text-gray-500" />
        </Link>
      </footer>
    </div>
  );
};

export default Dashboard;
