import React, { useState, useCallback } from 'react';
import { BookmarkIcon as BookmarkSolid } from '@heroicons/react/24/solid';
import { BookmarkIcon as BookmarkOutline } from '@heroicons/react/24/outline';

/**
 * Props:
 *  initialSaved?: boolean  (uncontrolled start)
 *  saved?: boolean         (controlled)
 *  onChange?: (saved:boolean) => void
 *  size?: number           (icon size px)
 *  className?: string
 */
const SaveButton = ({
  initialSaved = false,
  saved,
  onChange,
  size = 24,
  className = '',
}) => {
  const [inner, setInner] = useState(initialSaved);
  const isControlled = saved !== undefined;
  const isSaved = isControlled ? saved : inner;

  const toggle = useCallback(() => {
    const next = !isSaved;
    if (!isControlled) setInner(next);
    onChange && onChange(next);
  }, [isSaved, isControlled, onChange]);

  const Icon = isSaved ? BookmarkSolid : BookmarkOutline;

  return (
    <button
      type="button"
      onClick={toggle}
      aria-pressed={isSaved}
      aria-label={isSaved ? 'Remover dos favoritos' : 'Salvar nos favoritos'}
      className={`relative inline-flex items-center justify-center rounded-full transition
        ${isSaved ? 'bg-brand-red/10' : 'bg-gray-100 hover:bg-gray-200'}
        ${className}`}
      style={{ width: size + 12, height: size + 12 }}
    >
      <Icon
        className={`transition-transform ${
          isSaved ? 'text-brand-red scale-110' : 'text-gray-600'
        }`}
        style={{ width: size, height: size }}
      />
    </button>
  );
};

export default SaveButton;
