import React from 'react';
import { Link } from 'react-router-dom';
import { BookmarkIcon } from '@heroicons/react/24/solid';
import NavBar from '../components/NavBar';
import { useMenu } from '../components/MenuContext';
import SaveButton from '../components/SaveButton';

// Substitua pelos imports reais das imagens
import img1 from '../imgs/makeRareBeauty.jpg';
import img2 from '../imgs/cadeirasNaGrama.jpg';
import img3 from '../imgs/predio.jpg';

const newsData = [
  { id: 1, category: 'Funcionais e acessíveis', title: 'Selena Gomez revoluciona o mercado de embalagens de maquiagem com Rare Beauty.', image: img1, date: '15/11/25' },
  { id: 2, category: 'Música e Cultura', title: 'Como Bad Bunny usou seu novo disco para defender a história e a cultura de Porto Rico', image: img2, date: '14/11/25' },
  { id: 3, category: 'Arquitetura e Arte', title: 'Edifício Copan: Tudo sobre o grande ícone da arquitetura moderna em São Paulo', image: img3, date: '13/11/25' },
];

const NewsletterList = () => {
  const { toggleMenu } = useMenu();
  return (
    <div className="bg-white min-h-screen pb-32">
      <header className="p-4 flex items-center gap-4">
        <button
          onClick={toggleMenu}
          className="h-11 w-11 flex-shrink-0 bg-gray-900 text-white rounded-full flex items-center justify-center"
        >
          <span className="block w-5 h-5 relative">
            <span className="absolute inset-x-0 top-0 h-[3px] bg-white rounded" />
            <span className="absolute inset-x-0 top-1/2 -translate-y-1/2 h-[3px] bg-white rounded" />
            <span className="absolute inset-x-0 bottom-0 h-[3px] bg-white rounded" />
          </span>
        </button>
        <div className="flex-grow bg-brand-red text-white rounded-full py-2 px-6 flex justify-between items-center text-xl font-bold">
          <span>Newsletter</span>
          <BookmarkIcon className="h-6 w-6" />
        </div>
      </header>

      <main className="px-4 space-y-6">
        {newsData.map((item) => (
          <div
            key={item.id}
            className="bg-white rounded-3xl shadow overflow-hidden relative"
          >
            <Link to={`/article/${item.id}`}>
              <div className="relative">
                <img
                  src={item.image}
                  alt={item.title}
                  className="w-full h-48 object-cover bg-gray-200"
                />
                <div className="absolute top-3 right-3 bg-white/90 text-gray-800 text-xs font-bold px-3 py-1 rounded-full">
                  {item.date}
                </div>
              </div>
            </Link>
            <div className="p-4 pr-16">
              <p className="text-sm text-gray-500">{item.category}</p>
              <h3 className="text-base font-bold text-gray-800 leading-tight mt-1">
                {item.title}
              </h3>
            </div>
            <SaveButton initialSaved={false} size={22} className="absolute bottom-4 right-4" />
          </div>
        ))}
      </main>
      <NavBar />
    </div>
  );
};

export default NewsletterList;
