import React from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    navigate('/welcome');
  };

  return (
    <div className="min-h-screen w-full flex items-center justify-center bg-white px-6">
      <div className="w-full max-w-md flex flex-col">
        <h2 className="text-4xl font-bold text-gray-800 mb-2 text-center">Fa√ßa Login</h2>
        <p className="text-gray-600 mb-8 text-center">
          Para participar da comunidade, se tornar um designer inovador e mestre
          dos processos, entre com seu <strong className="text-gray-800">e-mail institucional!</strong>
        </p>
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label htmlFor="email" className="block text-sm font-medium text-gray-500 mb-2">E-mail:</label>
            <input
              type="email"
              id="email"
              className="w-full p-3 bg-white rounded-xl shadow-[0_0_0_2px_rgba(0,0,0,0.15)] focus:outline-none focus:ring-2 focus:ring-brand-red focus:shadow-[0_0_0_2px_rgba(239,68,68,0.55)] transition"
            />
          </div>
          <div>
            <label htmlFor="password" className="block text-sm font-medium text-gray-500 mb-2">Senha:</label>
            <input
              type="password"
              id="password"
              className="w-full p-3 bg-white rounded-xl shadow-[0_0_0_2px_rgba(0,0,0,0.15)] focus:outline-none focus:ring-2 focus:ring-brand-red focus:shadow-[0_0_0_2px_rgba(239,68,68,0.55)] transition"
            />
          </div>
          <button
            type="submit"
            className="w-full bg-brand-red text-white py-3 rounded-full font-bold text-lg shadow-[0_6px_18px_-4px_rgba(239,68,68,0.55)] active:scale-[.98] transition"
          >
            Entrar
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
